scripts.script:
                            
log.error("I am here")
def userProp = session.getUserProperties()
assert userProp != null
def skipAllTestsVar = 'mySkipTests'
log.debug("using variable $skipAllTestsVar as the skip all tests variable")
def skipTests = userProp[skipAllTestsVar]
log.debug("value of property '$skipAllTestsVar' is $skipTests")
def doSkipAllTests = false
if(skipTests == null)
{
  skipTests = "false"
  doSkipAllTests = false
}
else if(skipTests.equalsIgnoreCase("true"))
{
  doSkipAllTests = true
}

if(doSkipAllTests)
{
    log.debug("Skipping all tests based on property " + skipAllTestsVar) 
}

def graph = session.projectDependencyGraph
def projs = graph.sortedProjects

projs.each() {
  def projName = it.name
  Boolean doSkipTestsForProj= null // better default?

  log.info("processing project " + projName)
  def var = it.name + '.skipTests'
  log.debug("Var = " + var)
  
  def projSkipTests = userProp[var]
  log.debug("property '$var' is $projSkipTests")
  if(projSkipTests != null)
  {
    log.debug("skip Tests not null for " + it.name)
    if(projSkipTests.equalsIgnoreCase('true'))
    {
      doSkipTestsForProj = true
    }
    else if(projSkipTests.equalsIgnoreCase('false'))
    {
      doSkipTestsForProj = false
    }
    else
    {
      throw new RuntimeException("Invalid value for user property '" + var + "'")
    }
  }

  def skipTestsVal = false
  if((doSkipAllTests && doSkipTestsForProj != false) || doSkipTestsForProj == true)
  {
    log.debug('skipping tests for ' +  it.name)
    skipTestsVal = true
  }
  
  def plugin = it.build.pluginsAsMap['org.apache.maven.plugins:maven-surefire-plugin']
  if(plugin != null)
  {
    log.debug("Found surefire plugin")
    if(skipTestsVal)
    {
        log.debug("setting executions for " + it.name + " surefire to []")
        plugin.executions = []
    }
  }
  else
  {
      log.debug("did not find surefire plugin")
  }
}
